import { useState } from "react";

function AddTransaction({sendData}) {

let INITIAL = {
    username: localStorage.getItem("username"),
    "amount":0.00,
    "date":"MM-DD-YYYY",
    "description":""


}

    const [formData, setFormData] = useState(INITIAL);




        const handleChange = evt => {

            const { name, value } = evt.target;
            setFormData(fData => ({
              ...fData,
              [name]: value
            }));
          }

          function submitForm(event){
              event.preventDefault()

              return sendData(formData)

          }



    return (

        <section className="intro transaction">
            <div className="mask d-flex align-items-center h-100" style={{ backgroundColor: "#D6D6D6" }}>
                <div className="container">
                    <form className="row justify-content-center" onChange={handleChange} onSubmit={submitForm}>
                        <div className="col-12 col-md-8 col-lg-6 col-xl-5">
                            <div className="card" style={{ borderRadius: "1rem" }}>
                                <div className="card-body p-5 text-center">

                                    <div className="my-md-5 pb-5">

                                        <h1 className="fw-bold mb-3">                        <span className="d-inline-block me-2">
                                        <svg xmlns="http://www.w3.org/2000/svg" height="24px" viewBox="0 0 24 24" width="24px" fill="#ffffff"><path d="M0 0h24v24H0z" fill="none"/><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1.41 16.09V20h-2.67v-1.93c-1.71-.36-3.16-1.46-3.27-3.4h1.96c.1 1.05.82 1.87 2.65 1.87 1.96 0 2.4-.98 2.4-1.59 0-.83-.44-1.61-2.67-2.14-2.48-.6-4.18-1.62-4.18-3.67 0-1.72 1.39-2.84 3.11-3.21V4h2.67v1.95c1.86.45 2.79 1.86 2.85 3.39H14.3c-.05-1.11-.64-1.87-2.22-1.87-1.5 0-2.4.68-2.4 1.64 0 .84.65 1.39 2.67 1.91s4.18 1.39 4.18 3.91c-.01 1.83-1.38 2.83-3.12 3.16z"/></svg>
                                        </span></h1>

                                        <i className="fas fa-user-astronaut fa-3x my-5"></i>

                                        <div className="form-outline mb-2">
                                            <label className="form-label" htmlFor="firstName">Description</label>
                                            <input name="firstName" type="input" id="firstName" className="form-control form-control-lg" autoComplete="given-name"/>
                                        </div>

                                        <div className="form-outline mb-4">
                                        <input name="goal" type="number" step=".01" id="form6Example6" className="form-control" placeholder="Goal" />
                                        <label className="form-label" htmlFor="form6Example6">Amount</label>
                                      </div>

                                        <button className="btn btn-primary btn-lg btn-rounded gradient-custom text-body px-5" type="submit">Add Transaction</button>

                                    </div>


                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </section>

    )

}


export default AddTransaction;
